jQuery.fn.disableTextSelect=function(){return this.each(function(){$(this).css({MozUserSelect:"none",webkitUserSelect:"none"}).attr("unselectable","on").on("selectstart",function(e){return $(e.target).is("input, textarea, select, option, button, html")}).on("cut copy paste",function(e){e.preventDefault()})})};jQuery.fn.enableTextSelect=function(){return this.each(function(){$(this).css({MozUserSelect:"",webkitUserSelect:""}).attr("unselectable","off").off("selectstart").off("cut copy paste")})};